class Solution:
    def __init__(self):
        self.dp = {}

    def solve(self, k, i, jump, can):
        if i > k + 5:
            return 0

        if jump > 31:
            return 0

        if (i, jump, can) in self.dp:
            return self.dp[(i, jump, can)]

        ans = 1 if i == k else 0

        if can:
            ans += self.solve(k, i - 1, jump, False)

        if i + (1 << jump) <= k + 1:
            ans += self.solve(k, i + (1 << jump), jump + 1, True)

        self.dp[(i, jump, can)] = ans

        return ans

    def waysToReachStair(self, k: int) -> int:

        return self.solve(k, 1, 0, True)

solution = Solution()
result = solution.waysToReachStair(10)
print(result)
