class Solution:
    def waysToReachStair(self, k: int) -> int:
        dp = {}
        def f(i, jump, can_back):
            key = (i, jump, can_back)
            if key in dp:
                return dp[key]
            #print(f'{i} {jump} {can_back}')
            if i > k + 3:
                return 0
            ans = 0
            if i == k:
                ans += 1
            if can_back and i > 0:
                ans += f(i - 1, jump, False)
            ans += f(i + 2 ** jump, jump + 1, True)
            dp[key] = ans
            return ans;
        return f(1, 0, True)
