#include <unordered_map>
using namespace std;

class Solution {
public:
    int f(int i, int jump, bool c, int k, unordered_map<int, int>& memo) {
        if (i > k + 1) return 0;
        if (jump >= 32) return 0;
        int key = (i << 10) | (jump << 1) | c;
        if (memo.find(key) != memo.end()) return memo[key];

        int ans = 0;
        if (i == k) ans = 1;
        if (!c) ans += f(i - 1, jump, 1, k, memo);
        ans += f(i + (1 << jump), jump + 1, 0, k, memo);

        memo[key] = ans;
        return ans;
    }

    int waysToReachStair(int k) {
        unordered_map<int, int> memo;
        return f(1, 0, 0, k, memo);
    }
};
