class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k < 4:
            return [2,4,4,3][k]
        ans = 0
        k -= 1
        bit = 1
        cnt = 0
        while cnt < 3:
            x = pow(2,bit) - 1
            if x > k:
                cnt += 1

            if x - (bit+1) <= k <= x:
                n,r = bit+1,x-k
                ans += comb(n,r)
            bit += 1
        return ans
