class Solution {
public:
    int waysToReachStair(int k) {
        int res=0;
        int p=1;

        unordered_map<int, int> M;
        M[1]=1;

        while(!M.empty()) {
            unordered_map<int, int> M2=M;

            for (auto& [a, b] : M) {
                if (a==k) res+=b;
                if (a-1 == k) res+=b;
                M2[a-1]+=b;
            }

            unordered_map<int, int> M3;

            for (auto& [a, b] : M2) {
                if (a+p <= k+1) {
                    M3[a+p]+=b;
                }
            }

            p<<=1;
            swap(M3, M);
        }

        return res;
    }
};
