class Solution {
public:
    vector<vector<vector<int>>>dp;
    int f(int k,long long stair,int jump,int back,int backs){
        if(stair>k+1 || stair<0) return 0;
        if(dp[jump][backs][back]!=-1) return dp[jump][backs][back];
        int ans=stair==k;
        ans+=f(k,stair+pow(2,jump),jump+1,0,backs);
        if(back==0) ans+=f(k,stair-1,jump,1,backs+1);
        return dp[jump][backs][back]=ans;
    }
    int waysToReachStair(int k) {
        dp=*new vector<vector<vector<int>>>(32,vector<vector<int>>(35,vector<int>(2,-1)));
        return f(k,1,0,0,0);
    }
};
