#define ll long long

class Solution {
public:
    ll solve(ll k, ll ind, ll jump, ll f,map<ll,vector<vector<ll>>> &dp) {
        if (ind-5>k)
            return 0;
        if(jump>31)
            return 0;
        if((dp.find(ind)!=dp.end()) && dp[ind][jump][f] != -1)
            return dp[ind][jump][f];

        ll ans=(ind==k);
        if (f)
            ans += solve(k, ind-1, jump, 0,dp);
        if (ind+pow(2,jump)<=(k+1))
            ans += solve(k, ind+pow(2,jump), jump + 1, 1,dp);

        dp[ind].resize(32, vector<ll>(2, -1));
        return dp[ind][jump][f] = ans;
    }
    int waysToReachStair(int k) {
        map<ll, vector<vector<ll>>> dp;
        return solve(k, 1, 0, 1,dp);
    } 
};
