class Solution:
    def waysToReachStair(self, k: int) -> int:
        k = k - 1
        jumps = 0         
        res = 0 if k > 0 else 1

        cur = 0 
        for jumps in range(32):
            cur += 2 ** jumps 
            gap = cur - k 
            if 0<= gap <= jumps + 2: 
                res += comb(jumps+2, gap)

        return res
