class Solution {
public:
    int k_;
    unordered_map<string,int> mp;

    int dfs(int curr, int jump, bool minus){
        int ans = curr == k_ ? 1 : 0;

        string key = to_string(curr) + '-' + to_string(jump) + '-' + to_string(minus);

        if(mp.count(key))
            return mp[key];

        if(curr > k_ + 1)
                return 0;

        if(curr -1 >= 0 && minus)
            ans += dfs(curr-1, jump, false);

        ans += dfs(curr+ pow(2, jump), jump+1, true);
        return mp[key] = ans;
    }

    int waysToReachStair(int k) {
        k_ = k; 

        int ans = dfs(1, 0, true);
        return ans;
    }
};
