class Solution {
public:
    map<int,vector<vector<int>>>dp;
    int check(int k,int i,int j,bool can){
        if(i>k+5||j>31)return 0;
        if(dp.count(i) and dp[i][j][can]!=-1){
            return dp[i][j][can];

        }
        int ans=(i==k);
        if(can){
            ans+=check(k,i-1,j,0);
        }
        if(i+(1<<j)<=k+1){
            ans+=check(k,i+(1<<j),j+1,1);
        }
        dp[i].resize(32,vector<int>(2,-1));
        return dp[i][j][can]=ans;
    }
    int waysToReachStair(int k) {
        return check(k,1,0,1);
    }
};
