class Solution {
public:
    const int MOD = 1e9 + 7;
    int dp[32];
    long long fact[32];
    long long ifact[32];

    int waysToReachStair(int k) {
        dp[0] = 0;
        dp[1] = 1;
        for(int i=2;i<32;i++) dp[i] = 2 * dp[i-1];
        for(int i=2;i<32;i++) dp[i] += dp[i-1];

        fact[0] = 1;
        for (int i = 1; i < 32; i++)
            fact[i] = (fact[i - 1] * i) % MOD;

        ifact[31] = mod_exp(fact[31], MOD - 2, MOD);
        for (int i = 30; i >= 0; i--)
            ifact[i] = (ifact[i + 1] * (i + 1)) % MOD;

        long long ans = 0;
        for(int i=0;i<32;i++) {
            if(dp[i] + 1 < k) continue;
            if(dp[i] + 1 - k > i + 1) break;

            ans += nCr(i + 1, dp[i] + 1 - k);
            ans %= MOD;
        }
        return ans;
    }

    int nCr(int n, int r) {
        if(r > n) return 0;
        return (fact[n] * ifact[r] % MOD * ifact[n - r] % MOD) % MOD;
    }

    int mod_exp(int x, int y, int MOD)
    {
        long long result = 1;
        x = x % MOD;
        while (y > 0)
        {
            if (y & 1)
            {
                result = (result * x) % MOD;
            }
            y = y >> 1;
            x = ((long long)x * x) % MOD;
        }
        return result;
    }
};
