from functools import lru_cache
class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache
        def helper(cur, jump, used):
            res = 0
            if cur == k:
                res += 1
            if cur > k+1:
                return 0
            if not used and cur>0:
                res += helper(cur-1, jump, True)
            return res + helper(cur+2**jump, jump+1, False)
        return helper(1, 0, False)
