typedef long long ll;
class Solution {
public:
    unordered_map<ll, unordered_map<ll, unordered_map<ll, ll>>> dp;
    ll solve(ll i, ll j, ll flag, ll k) {

        if(dp.find(i)!=dp.end() && dp[i].find(j)!=dp[i].end() && dp[i][j].find(flag)!=dp[i][j].end()) {
            return dp[i][j][flag];
        }

        if(j == 32) {
            return dp[i][j][flag] = 0;
        }

        ll ans = (i==k);

        if(flag == 0 && i!=0) ans += solve(i-1, j, 1, k);

        ans += solve(i + (ll)pow(2ll, j), j+1, 0, k);

        return dp[i][j][flag] = ans;
    }
    int waysToReachStair(int k) {
        return solve(1, 0, 0, k);
    }
};
