class Solution {
public:
    int waysToReachStair(int k) {
        //cout<<"k: "<<k<<endl;
        int ans = 0;
        k=k-1;
        if(k==0)ans++;
        if(k==-1)ans++;
        for(int i=0;i<30;i++)
        {
            long long one =1;
            long long mask = (one<<(i+1))-1;
            int ones = mask-k;
            if(i+2<ones || ones<0)continue;
            long long count=1;
            for(int k=i+2;k>i+2-ones;k--)
            {
                count*=k;
                count/=i+2-k+1;
            }
            ans+=count;
            //cout<<i<<" "<<count<<endl;
        }
        return ans;
    }
};
