class Solution {
    unordered_map<int, unordered_map<int, unordered_map<int, int>>> dp;
public:
    int waysToReachStair(int k, bool flag = true, int curr = 1, int jump = 0) {
        if(curr > k+1 || curr < 0)  return 0;
        if(dp[curr][jump][flag])    return dp[curr][jump][flag]-1;
        int a = waysToReachStair(k, true, curr+pow(2, jump), jump+1);
        int b = 0;
        if(flag) b = waysToReachStair(k, false, curr-1, jump);
        dp[curr][jump][flag]=(curr==k)+a+b+1;
        return (curr==k)+a+b;
    }
};
