class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def solve(current_stair, jump, backstep_availiable):
            ans = 0
            if current_stair == k:
                ans += 1
            if current_stair > k+1:
                return 0
            if backstep_availiable:
                ans += solve(current_stair - 1, jump, False)
            ans += solve(current_stair + (1<<jump), jump+1, True) 
            return ans
        return solve(1, 0, True)
