class Solution {
public:
    int wtrs(long long k,int j,int d,vector<unordered_map<int,unordered_map<int,int>>> &h){
        if(k<0){
            return 0;
        }
        if(h[d][k].count(j)){
            return h[d][k][j];
        }
        int a=0,b=0;
        if(k==1)a++;
        // cout<<k<<endl;
        if(d){
            a+=wtrs(k+1,j,0,h);
        }
        b=wtrs(k-pow(2,j),j+1,1,h);

        return h[d][k][j]=a+b;
    }
    int waysToReachStair(int k) {
        // cout<<(2-pow(2,0))<<endl;
        // unordered_map<int,unordered_map<int,pair<int,int>>> h;
        vector<unordered_map<int,unordered_map<int,int>>> h(2);
        return wtrs(k,0,1,h);
    }
};
