class Solution {
public:
    vector<map<long long,map<long long,int>>> dp;
    int solve(long long i, long long k, long long jump, bool isC)
    {
        if(i>k+3)
            return 0;
        if(dp[isC].count(jump)&&dp[isC][jump].count(i))
            return dp[isC][jump][i];
        int ans=0;
        if(i==k)
            ans++;
        if(i!=0&&!isC)
            ans+=solve(i-1,k,jump,1);
        ans+=solve(i+jump,k,jump*2,0);
        return dp[isC][jump][i]=ans;

    }
    int waysToReachStair(int k) {
        dp = vector<map<long long,map<long long,int>>>(2);
        return solve(1,k,1,0);
    }
};
