class Solution {
public:
    int k;
    map<pair<pair<int,int>,int>,int> mp;
    int sol(int i, int prev, int jump){
        if(i<0 || i>k+1) return 0;
        if(mp.find({{i,prev},jump}) != mp.end()) return mp[{{i,prev},jump}];
        int ans = 0;
        if(i==k) ans++;
        if(prev==0) ans+= sol(i+(1<<jump), 1, jump+1);
        else ans+= sol(i-1,0,jump) + sol(i+(1<<jump),1,jump+1);
        return mp[{{i,prev},jump}] = ans;
    }
    int waysToReachStair(int _k) {
        k = _k;
        return sol(1,2,0);
    }
};
