class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(i, jump, can_jump):
            if i > k + 5: return 0
            if jump > 31: return 0

            res = i == k
            if i > 0 and can_jump:
                res += dfs(i - 1, jump, 0)
            if i + (1 << jump) <= k + 1:
                res += dfs(i + (1 << jump), jump + 1, 1)
            return res

        return dfs(1, 0, 1)
