class Solution {
    unordered_map<int, vector<vector<int>>>mp;
    int countWays(int k, int i, int j, bool can_jump) {
        if(i>k+5){
            return 0;
        }
        if(j>31){
            return 0;
        }
        if(mp.count(i) && mp[i][j][can_jump]!=-1)
            return mp[i][j][can_jump];

        int res=(i==k);
        if(can_jump){
            res += countWays(k,i-1,j,0);
        }
        if (i+(1<<j)<=k+1){
            res+=countWays(k,i+(1<<j),j+1,1);
        }
        mp[i].resize(32,vector<int>(2,-1));
        return mp[i][j][can_jump]=res;
    }
public:
    int waysToReachStair(int k) {
        return countWays(k,1,0,1);
    }
};
