class Solution {
public:
    int dp[100][100];
    int func(int f,int s,int k){


        long long pos=((1LL)<<s)-1-f;
        if(pos==k)return 1;
        if(pos>k)return 0;

        if(dp[f][s]!=-1)return dp[f][s];
        int tk1=func(f+1,s+1,k);
        int tk2=func(f,s+1,k);


        return dp[f][s]=tk1+tk2;



    }
    int waysToReachStair(int k) {

        memset(dp,-1,sizeof(dp));
        if(k==0)return 2;
        if(k==1)return 4;
        int ans=func(0,0,k-1);
        memset(dp,-1,sizeof(dp));
        ans+=func(0,0,k);
        return  ans;

    }
};
