class Solution {
public:
    int nCr(int n, int r) {
    if (r > n) return 0;
    if (r == 0 || n == r) return 1;
    double ans = 0;
    for (int i = 0; i < r; i++) {
        ans += log(n - i) - log(i + 1);
    }
    return (int)round(exp(ans));
}
    int waysToReachStair(int k) {
        if(k==0 || k==4)return 2;
        if(k<=2 )return 4;
        if(k==3)return 3;
        int lg = log2(k);
        int x=1;
        for(int i=0;i<lg;i++)x*=2;
        if(x!=k){
            x*=2;
            lg++;
        }
        int y=x-k;
        lg++;
        return (y>x)?0:nCr(lg,y);
    }
};
