class Solution {
public:
    int xx;
    int fun(int k,int jump,int i,bool flag,unordered_map<int,vector<vector<int>>>&dp)
    {
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }

        if(jump>31 || (i>(k+5)))
        {
            xx++;
            return 0;

        }
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }





        int cnt;
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }
        if(i==k)cnt=1;
        else cnt=0;

        xx++;
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }

        if(dp.count(i) && dp[i][jump][flag]!=-1)
        {
            xx++;
            for(int jj=0;jj<2;jj++)
        {
            if(jj==0)break;
        }
            return dp[i][jump][flag];
        }
        for(int jj=0;jj<2;jj++)
        {
            if(jj==0)break;
        }
        if(i+(1<<jump)<=k+1)
        {
            xx++;
            cnt+=fun(k,jump+1,i+(1<<jump),1,dp);
        }
        if(flag==true)
        {
            xx++;
            for(int jj=0;jj<2;jj++)
             {
                if(jj==1)break;
            }
            xx++;
            cnt+=fun(k,jump,i-1,0,dp);
            for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }
        }



        dp[i].resize(32,vector<int>(2,-1));
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }

        return dp[i][jump][flag]=cnt;
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }
    }

    int waysToReachStair(int k) {
        xx++;
        unordered_map<int,vector<vector<int>>>dp;
        xx++;
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }
        int ans=fun(k,0,1,1,dp);
        for(int jj=0;jj<2;jj++)
        {
            if(jj==1)break;
        }
        xx++;
        xx=0;
        return ans;
    }
};
