class Solution {
public:
   int ans(int k,int i,int j,bool f,unordered_map<int,unordered_map<int,unordered_map<bool,int>>>&v){
        if(i<0 || i>k+1)return 0;
        int i1=0,i2=0;
        if(v[i][j].find(f)!=v[i][j].end())return v[i][j][f];
        if(i==k){
           if(f)i1=ans(k,i-1,j,0,v);
           i2=ans(k,i+(1<<j),j+1,1,v);
            return v[i][j][f]=i1+i2+1;
        }
        if(f) i1=ans(k,i-1,j,0,v);
         i2=ans(k,i+(1<<j),j+1,1,v);
        return v[i][j][f]=i1+i2;
    }
    int waysToReachStair(int k) {
        unordered_map<int,unordered_map<int,unordered_map<bool,int>>>v;
        return ans(k,1,0,1,v);
    }
};
