class Solution {
public:
    int waysToReachStair(int k) {
        map<LL, LL> ct;
        ct[0] = 2;
        ct[1] = 4;
        ct[2] = 4;
        ct[3] = 3;
        ct[4] = 2;
        ct[5] = 4;
        ct[6] = 6;
        ct[7] = 4;
        ct[8] = 1;

        LL c = 11, x = 15, len;
        VL a = { 1,5,10,10,5,1 }, aa;

        while (c <= 1E9) {
            len = a.size();

            FOR(i, len) ct[c + i] = a[i];

            aa.clear();
            aa.push_back(1);
            FOR(i, len - 1) aa.push_back(a[i] + a[i + 1]);
            aa.push_back(1);

            c += x;
            x = x * 2 + 1;

            swap(a, aa);
        }

        return ct[k];
    }
