class Solution {
public:
    /*
    1
    -1 1 -1 2
    1 -1
    */
    long long ncr(int n,int r)
    {
        long long ans = 1;
        for(int i = 1;i<=r;i++)
        {
            ans *= ((n - r + i));
            ans /= i;
        }
        return ans;
    }
    int waysToReachStair(int k) {
        int ans = 0;
        for(int jumps = 0;jumps<=31;jumps++)
        {
            int sum = pow(2,jumps);
            int minus = sum - k;
            if(minus<0)
            {
                continue;
            }
            if(minus>(jumps + 1))
            {
                break;
            }
            ans += ncr(jumps + 1,minus);
        }
        return ans;
    }
};
