class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dfs(current, step, last0) -> int:
            if current > k + 1:
                return 0

            ans = 0
            if current == k:
                ans += 1

            if current != 0 and not last0:
                ans += dfs(current - 1, step, True)

            ans += dfs(current + 2**step, step + 1, False)
            return ans

        return dfs(1, 0, False)
