class Solution:
    def waysToReachStair(self, k: int) -> int:
        maxjump = 35
        f = [defaultdict(int) for _ in range(2)]
        f[1][1] = 1
        ans = 0
        for jump in range(maxjump):
            newf = [defaultdict(int) for _ in range(2)]
            for fb in [1, 0]:
                for step, cur in f[fb].items():
                    if step == k:
                        ans += cur
                    if fb == 1:
                        if step - 1 >= 0:
                            f[0][step-1] += cur
                        if step + (1<<jump) < k+10:
                            newf[1][step + (1<<jump)] += cur
                    else:
                        if step + (1<<jump) < k+10:
                            newf[1][step + (1<<jump)] += cur
            # print(jump)
            # print(ans)
            # print(f)
            # print(newf)

            f = newf
        return ans
