class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def fn(x, j, f): 
            """Return """
            ans = 0 
            if x == k: ans += 1
            if x > k+1: return 0
            ans += fn(x+pow(2, j), j+1, 0)
            if f == 0 and x: ans += fn(x-1, j, 1)
            return ans 

        return fn(1, 0, 0)
