class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i=1, jp=0, prev=-1):
            if i > k + 1:
                return 0
            res = i == k
            if prev == 0:
                res += dp(i+pow(2, jp), jp+1, 1)
            else:
                res += dp(i+pow(2, jp), jp+1, 1) + dp(i-1, jp, 0)
            return res
        return dp()
