class Solution {
public:

    map<pair<int, int>, int> memo;

    int solve(int k, int jump) {
        pair<int, int> key = {k, jump};
        if(memo.count(key))
            return memo[key];
        if(k < -1)
            return 0;
        // if(k == -1)
        //     return 1 + solve(k-(1<<jump), jump+1);
        memo[key] = (k == 0 || k == -1) + solve(k+1-(1<<jump), jump+1) + solve(k-(1<<jump), jump+1);
        cout<<"solve for k = "<<k<<", jump = "<<jump<<" gives "<<memo[key]<<endl; 
        return memo[key];
    }
    int waysToReachStair(int k) {
        return solve(k-1, 0);
    }
};
