class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def solve(cur,jump):

            res=  0
            if (cur==k):
                res+=1
            elif (cur-1>k):
                return 0
            if (cur-1==k):
                res+=1
            res+=solve(cur-1+(2**jump),jump+1)
            res+=solve(cur+(2**jump),jump+1)

            return res
        return solve(1,0)
