template<typename T> T ncr(T n, T k){
    if (n < k){return 0;}
    k = min(k, n - k); T ans = 1;
    for (int i = 1; i <= k; i++) {ans *= (n - i + 1), ans /= i;}
    return ans;
}
class Solution {
public:
    int waysToReachStair(int k) {
        int ans = 0;
        for (int i = 0; i < 30; i++){
            int s = (1 << i);
            for (int j = 0; j <= i + 1; j++){
                int p = s - j;
                if (p == k) ans += ncr((i + 1) * 1LL, j * 1LL);
            }
        }
        return ans;
    }
};
