#define _int64 long long

_int64 c[60][60];

class Solution {
public:
    int waysToReachStair(int k) {
        _int64 i,j,tot,nn,ans;
        k--;
        for (i=0;i<60;i++)
        {
            c[i][0]=1;c[i][i]=1;
            for (j=1;j<i;j++)
                c[i][j]=c[i-1][j-1]+c[i-1][j];
        }
        ans=0;
        if (k==-1) ans++;
        if (k==0) ans++;
        for (i=0;i<=30;i++)
        {
            tot=(1LL<<(i+1))-1;
            nn=i+2;
            if ((tot>=k)&&(tot-k<=nn))
            {
                ans+=c[nn][tot-k];
            }
        }
        return (int)ans;
    }
};
