class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(curr, step, can):
            if curr > (k + 1):
                return 0;
            if curr < -1:
                return 0;
            ret = 0;
            if curr == k:
                ret += 1;
            ret += dfs(curr + (1 << step), step + 1, 1);
            if can == 1:
                ret += dfs(curr - 1, step, 0)
            return ret

        return dfs(1, 0, 1)
