class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k==0: return 2
        state = {(1,0,0): 1} # (i,jump,down)
        done = False
        result = 0
        while state:
            done = True
            new_state = collections.defaultdict(int)
            for i,jump,down in state:
                if i==k: result += state[i,jump,down]
                if not down:
                    new_state[i-1,jump,1] += state[i,jump,down]
                j = i + 2**jump
                if j<=k+1:
                    new_state[j,jump+1,0] += state[i,jump,down]
            state = new_state
            #print(result, state)
        return result
