class Solution {
public:

    int calc(int n, int k) {
        k = min(k, n - k);
        long long ret = 1ll;
        for(long long i = 1; i <= k; i++) {
            ret *= (1ll) * (n - k + i);
            ret /= i;
        }
        return (int)ret;
    }

    int waysToReachStair(int k) {
        int ret = 0;
        for(int i = 0; i <= 30; i++) {
            int sm = 0;
            int vl = 1;
            for(int j = 0; j < i; j++) {
                sm += vl;
                vl *= 2;
            }
            if(sm + 1 >= k and sm + 1 - (i + 1) <= k) {
                int dif = sm + 1 - k;
                ret += calc(i + 1, dif);
            }
        }
        return ret;
    }
};
