class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(n, jump, prev_down):
            if n - 1 > k: return 0

            cnt = 1 if n == k else 0
            if prev_down is False:
                cnt += dfs(n-1, jump, True)
            cnt += dfs(n + 2 ** jump, jump+1,  False)
            # print(n, jump, prev_down, cnt)            
            return cnt

        return dfs(1, 0, False)
