class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(curr, lastdown, use):
            #print(curr, lastdown, use)
            if curr == k: 
                ans = 1
            else:
                ans = 0
            step = pow(2, use)
            if curr > k + 1:
                return 0
            if curr >= 1 and not lastdown:
                ans += dfs(curr - 1, True, use)
            ans += dfs(curr + step, False, use + 1)
            return ans

        ans = dfs(1, False, 0)
        return ans
