class Solution {
    public int waysToReachStair(int k) {

        long pow = 1;
        long powSum = 1;
        int cnt = 0;
        int ans = 0;

        while (true) {

            if (powSum < k) {
                cnt++;
                powSum += pow;
                pow *= 2;
                continue;
            }
            if (powSum - k > cnt + 1) {
                break;
            }
            int back = (int) (powSum - k);
            ans += C(cnt + 1, back);
//            System.out.printf("ps=%d k=%d C(%d %d)\n", powSum, k, cnt+1, back);

            cnt++;
            powSum += pow;
            pow *= 2;
        }
        return ans;
    }

    private int C(int n, int m) {
//        System.out.println(n +","+m);
        if (m == 0) {
            return 1;
        }
        if (m == 1) {
            return n;
        }

        if (n - m < m) {
            return C(n, n - m);
        }
        return C(n - 1, m) + C(n - 1, m - 1);
    }

    public static void main(String[] ar) {
        Solution sol = new Solution();
        System.out.println(sol.waysToReachStair(2));
    }
}
