class Solution {
public:
    long long table[50][50];
    long long choose(int n, int k){
        if(n<k) return 0;
        if(n==k) return 1;
        if(k==0) return 1; 
        if(table[n][k]!=-1){
            return table[n][k];
        }
        return table[n][k] = choose(n-1,k) + choose(n-1,k-1);
    }

    int waysToReachStair(int k) {
        long long location=1;
        long long ans=0;
        for(int i=0;i<50;i++){
            for(int j=0;j<50;j++){
                table[i][j]=-1;
            }
        }
        for(long long i=1, cnt=0;cnt<=40;i*=2,cnt++){
            long long amsub = cnt+1;
            if(location >= k && location - amsub <=k){
                long long needed = location - k;
                ans+= choose(amsub,needed);
            }
            location += i;
        }
        return ans;
    }
};
