import math

class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        p = 0
        while True:
            op1 = (2 ** p) - k
            if op1 < 0:
                p += 1
                continue
            if op1 <= p + 1:
                ans += math.comb(p+1, op1)
                # print(op1, p)
            else:
                break
            p += 1
        # print()
        return ans
