class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def count(i, jump, back):
            res = 1 if i == k else 0
            if not back:
                res += count(i - 1, jump, True)
            if i + jump - k <= 1:
                res += count(i + jump, jump * 2, False)
            return res
        return count(1, 1, False)
