class Solution(object):
    def comb(self, a, b):
        # a! / (b! * (a-b)!)
        ret = 1
        x = b
        y = (a - b)
        if x > y:
            x, y = y, x
        # x <= y
        # a! / (x! * y!)
        for i in range(y + 1, a + 1):
            ret *= i
        for i in range(1, x + 1):
            ret //= i
        return ret

    def waysToReachStair(self, k):
        """
        :type k: int
        :rtype: int
        """
        if k == 0:
            return 2
        tot = 1
        ans = 0
        if k == 1:
            ans += 1
        for i in range(31):
            tot += 1 << i
            if tot < k:
                continue
            if tot == k:
                ans += 1
                continue
            # tot > k
            diff = tot - k
            if diff > i + 2:
                continue
            ans += self.comb(i + 2, diff)
        return ans
