class Solution {
    map<int, map<int, map<bool, int>>> mp;

    int solve(int x, int pw, bool ls, int k){
        // cout <<x << " " << pw << " " << ls << endl;
        if(x > k+2 || x < 0)
            return 0;
        if(mp.find(x) != mp.end() && mp[x].find(pw) != mp[x].end() && mp[x][pw].find(ls) != mp[x][pw].end())
            return mp[x][pw][ls];

        int res = (x == k)? 1: 0;

        if(!ls && x!=0)
            res += solve(x-1, pw, 1, k);
        res += solve(x+pow(2,pw), pw+1, 0, k);
        return mp[x][pw][ls] = res;
    }

public:
    int waysToReachStair(int k) {
        return solve(1, 0, 0, k);
    }
};
