def fac(a):
    res=1
    for i in range(1,a+1):res*=i
    return res

def com(n,r):
    return fac(n)//fac(r)//fac(n-r)

class Solution:
    def waysToReachStair(self, k: int) -> int:
        vv=[2, 4, 4, 3, 2, 4, 6, 4, 1]
        if k<9:return vv[k]
        for i in range(4,32):
            r=2**i
            l=r-i-1
            if l<=k<=r:
                d=r-k
                return com(i+1,d)
        return 0
