class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def h(i,jmp,used):
            #print(i,jmp,used)
            if i>k:
                if used:return 0
                if i==k+1:
                    return h(i-1,jmp,True)

                return 0
            on_k = 1 if i==k else 0
            #if i==k:return 1
            if i==0:return on_k+h(i+2**jmp,jmp+1,False)
            if used:return on_k+h(i+2**jmp,jmp+1,False)

            ans=on_k


            #-1
            if not used:
                ans+=h(i-1,jmp,True)
            ans+=h(i+2**jmp,jmp+1,False)
            return ans
        return h(1,0,False)
