class Solution:
    def waysToReachStair(self, k: int) -> int:
        pwr = 0
        ub = 1
        lb = ub - pwr - 1

        ans = 0

        def C(n: int, r: int) -> int:
            if r < 0 or r > n:
                return 0

            r = min(r, n - r)
            ret = 1
            for i in range(1, r + 1):
                ret = ret * (n + 1 - i) // i

            return ret


        while lb <= k:
            if ub >= k:
                #print(pwr, lb, ub, f"C({pwr + 1}, {ub - k})")
                ans += C(pwr + 1, ub - k)
            pwr += 1
            ub <<= 1
            lb = ub - pwr - 1

        return ans
