/**
 * @param {number} k
 * @return {number}
 */
var waysToReachStair = function(k) {
    let res = 0;

    const mem = Array(32).fill().map(() => ({}));

    const dfs = (jump, cur) => {
        if (mem[jump][cur] !== undefined) return mem[jump][cur];
        mem[jump][cur] = 0;
        if (cur === k || cur === k + 1) {
            mem[jump][cur] = 1;
        }
        if (cur > k + 1) {
            return 0;
        }
        mem[jump][cur] += dfs(jump+1, cur + Math.pow(2, jump));
        if (cur > 0) {
            mem[jump][cur] += dfs(jump+1, cur - 1 + Math.pow(2, jump));
        }
        return mem[jump][cur];
    }
    return dfs(0, 1);
};
